import React, { useState, useEffect } from "react";
import "./Home.css"; // Ensure you create a separate CSS file for styling

const Home = () => {
  const [restaurants, setRestaurants] = useState([
    { id: 1, name: "The Spice House", cuisine: "Indian", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSkXZcd120aajvMOCoOjreIIrq6ykv5UIpUZg&s" },
    { id: 2, name: "Sushi World", cuisine: "Japanese", image: "data:image/jpeg;base64," },
    { id: 3, name: "Burger Haven", cuisine: "American", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQIWGU7eB45GsX-ieRT2UHudScdm4fXi3NSlg&s" },
    { id: 4, name: "Pasta Delight", cuisine: "Italian", image: "data:image/jpeg;base64," },
    { id: 5, name: "Taco Fiesta", cuisine: "Mexican", image: "data:image/jpeg;base64," },
    { id: 6, name: "Vegan Vibes", cuisine: "Vegan", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLcpJ6uzDdKhUDMH_hfayh5zozGALPAD72wQ&s" }
  ]);

  const [location, setLocation] = useState("Fetching location...");

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setLocation(`Lat: ${position.coords.latitude}, Lng: ${position.coords.longitude}`);
      },
      () => {
        setLocation("Location access denied");
      }
    );
  }, []);

  return (
    <div className="home-container">
      <header className="home-header">
        <h1>Discover Amazing Restaurants Near You</h1>
        <p className="location">üìç Your Location: {location}</p>
      </header>

      <div className="restaurant-grid">
        {restaurants.map((res) => (
          <div key={res.id} className="restaurant-card">
            <img src={res.image} alt={res.name} className="restaurant-image" />
            <div className="restaurant-info">
              <h2>{res.name}</h2>
              <p className="cuisine-type">{res.cuisine}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
